class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long result=0;
        unordered_map<long long,long long> prefixCnt;

        for(int i=0; i<n; i++){
            unordered_map<long long, long long> curr;
            if(nums[i]==k) result++;
            curr[nums[i]] = 1;

            for(auto& [value,frequency]: prefixCnt){
                long long newRes = value & nums[i];
                if(newRes == k){
                    result += frequency;
                }
                curr[newRes] += frequency;

            }
            prefixCnt = curr;
        }
        return result;

    }
};
