#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size(), ans=0;
        unordered_map<ll, ll> prev;
        for(ll num : nums){
            unordered_map<ll, ll> curr;
            curr[num]=1;
            if(num==k) ans++;
            for(auto &[v, f] : prev){
                if((v&num)==k) ans+=f;
                curr[(v&num)] += f;
            }
            prev=curr;
        }
        return ans;
    }
};
