class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        vector<vector<int>> idx(32);

        auto a = nums;
        int n = a.size();

        long long ans = 0;
        for (int i = 0; i < n; ++i) {
            for (int b = 0; b < 30; ++b) {
                if (!((a[i] >> b) & 1)) {
                    idx[b].push_back(i);
                }
            }
            int mx = i, mn = -1;
            for (int b = 0; b < 30; ++b) {
                if ((k >> b) & 1) {
                    if (!idx[b].empty()) {
                        mn = max(mn, idx[b].back());
                    } 
                } else {
                    if (!idx[b].empty()) {
                        mx = min(mx, idx[b].back());
                    } else {
                        mx = -1;
                    }
                }
            }
            if (mn < mx) {
                ans += (mx - mn);
            }
        }
        return ans;
    }
};
