/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
    const dp = new Array(nums.length).fill(0).map(()=>{return {}})
    const dynamo = (index,value) => {
        if(index >= nums.length){return 0}
        if(dp[index][value] !== undefined){return dp[index][value]}
        const n = value&nums[index]
        let next = dynamo(index+1,n)
        let plus = (n === k)?1:0
        dp[index][value] = next + plus
        return next+plus
    }
    let res = 0
    for(let i = 0;i<nums.length;i++){
        res += (dynamo(i,nums[i]))
    }
    return res
};
