#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll count = 0;
        map<ll, ll> mpPrevAnd;
        for (int i = 0; i < n; ++i) {
            map<ll, ll> mpCurrAnd;
            if (nums[i] == k)
                count++;
            mpCurrAnd[nums[i]] = 1;
            for (auto it : mpPrevAnd) {
                int andres = it.first & nums[i];
                if (andres == k)
                    count += it.second;
                mpCurrAnd[it.first & nums[i]] += it.second;
            }
            mpPrevAnd = mpCurrAnd;
        }

        return count;
    }
};
