#define intl long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        intl y = 0,i=0;
        map<intl, intl> m1;
        // for(auto i:nums) m1[nums[i]]++;
        while (i<nums.size()) {
            map<intl, intl> m2; 
            if (nums[i] == k) {
                y++;
            }
            m2[nums[i]]=1;
            for (auto it: m1) {
                if ((it.first&nums[i]) == k) {
                    y += it.second;
                }
                m2[it.first&nums[i]] += it.second;
            }

            m1 = m2;
            i++;
        }
        return y;
    }
};
