class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        unordered_map<long long,long long>mp;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>mp1;
            if(nums[i]==k){
                ans++;
            }
            mp1[nums[i]]=1;
            for(auto x:mp){
                long long temp=x.first & nums[i];
                if(temp==k){
                    ans+=x.second;
                }
                mp1[temp]+=x.second;
            }
            mp=mp1;
        }
        return ans;
    }
};
