        long long cnt = 0;
        unordered_map<long long ,long long> mp;
        for(auto num:nums){
            unordered_map<long long ,long long> st;
            if(num == target){
                cnt++;
            }

            st[num] = 1;
            for(auto [x,y]:mp){

                long long temp = x & num;
                if(temp == target){
                    cnt += y;
                }
                st[temp] += y;
            }
