class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
           int n=nums.size();
           ll cnt=0;
           unordered_map<ll,ll>mp;
        for(int i=0;i<n;++i){
            unordered_map<ll,ll>curr; 
            if(nums[i]==k){
                ++cnt;
            }
            curr[nums[i]]=1;
            for (auto it:mp){
                ll res=it.first&nums[i];
                if (res==k){
                    cnt+=it.second;
                }
                curr[res]+=it.second;
            }

            mp=curr;
        }
        return cnt;
    }
};
