#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll sum=0;
        unordered_map<ll,ll>mp;
        for(int i=0;i<n;i++){
            unordered_map<ll,ll>mp2;
            if(nums[i]==k)sum++;
            mp2[nums[i]]=1;
            for(auto [val,fre]:mp){
                ll ans = val&nums[i];
                if(ans==k){
                    sum+=fre;
                }
                mp2[ans]+=fre;
            }
            mp=mp2;
        }
        return sum;
    }
};
