class Solution {
public:
    long long countSubarrays(vector<int>& arr, int tgt) {
        int n = arr.size();
        long long cnt = 0;

        unordered_map<long long, long long> prevRes;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currRes; 

            if (arr[i] == tgt) {
                ++cnt;
            }
            currRes[arr[i]] = 1;

            for (auto& [v, f] : prevRes) {
                long long newRes = v & arr[i];
                if (newRes == tgt) {
                    cnt += f;
                }
                currRes[newRes] += f;
            }

            prevRes = currRes;
        }

        return cnt;
    }
};
