class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,long long> fr;
        int n = nums.size();
        int a = nums[0];
        fr[a]++;
        map<int,long long> feasible;
        feasible[a]++;
        for(int i = 1; i<n; ++i){
            map<int,long long> new_feasible;
            new_feasible[nums[i]]++;
            fr[nums[i]]++;
            for(auto &[el,f]:feasible){
                new_feasible[el&nums[i]]+=f;
                fr[el&nums[i]]+=f;
            }
            feasible = new_feasible;
        }
        return fr[k];
    }
};
