class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count =0;
        Map<Long,Long> map = new HashMap<>();

        for(int i=0;i<n;i++){
            Map<Long,Long> cmap = new HashMap<>();

        if(nums[i]==k){
            count++;
        }
            cmap.put((long)nums[i],1L);

            for(Map.Entry<Long,Long> e : map.entrySet()){
                long v = e.getKey();
                long f = e.getValue();

                long ne = v & nums[i];

                if(ne == k){
                    count+=f;
                }
                cmap.put(ne, cmap.getOrDefault(ne, 0L) + f);
            }
            map=cmap;
        }
        return count;
    }
}
