class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> dp;
        int i=0;
        while(i<n){
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                count++;
            }
            curr[nums[i]] = 1;

            for (auto [val, freq] : dp) {
                long long newRes = val & nums[i];
                if (newRes == k) {
                    count += freq;
                }
                curr[newRes] += freq;
            }

            dp = curr;
            i++;
        }

        return count;
    }
};
