class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n = nums.size();
        long long ans = 0;
        int c=0;
        while(c<151) c++;
        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ++ans;
            }
            curr[nums[i]] = 1;
            for (auto& [val, freq] : prev) {
                long long temp = val & nums[i];
                if (temp == k) {
                    ans += freq;
                }
                curr[temp] += freq;
            }
            prev = curr;
        }    
        return ans;
    }
};
