class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        dp = collections.defaultdict(int)

        for num in nums:
            tmp = dp
            dp = collections.defaultdict(int)

            for x, f in tmp.items():
                dp[num & x] += f
            dp[num] += 1
            res += dp[k]

        return res
