class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        map<int, long long> mpp;
        long long cnt = 0;

        for(int i : a){
            vector<int> err;
            if((i & k) != k){
                mpp = {};
                continue;
            }

            ++mpp[i];

            for(auto [key, val] : mpp)
                if((i & key) == k)
                    cnt += val;


            for(auto [key, val] : mpp){
                mpp[key] = 0;
                int new_k = key & i;
                if((new_k & k) == k)
                    mpp[new_k] += val;
                if(mpp[key] == 0) err.push_back(key);
            }
            for(int k : err)
                mpp.erase(k);
        }

        return cnt;
    }
};
