class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int arrLength = array.size();
        long long resultCount = 0;
        unordered_map<long long, long long> previousMap;

        for (int i = 0; i < arrLength; ++i) {
            unordered_map<long long, long long> currentMap;

            if (array[i] == target) ++resultCount;
            currentMap[array[i]] = 1;

            for (auto& elem : previousMap) {
                long long andResult = elem.first & array[i];
                if (andResult == target) resultCount += elem.second;
                currentMap[andResult] += elem.second;
            }
            previousMap = currentMap;
        }
        return resultCount;
    }
};
