class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        co, p = 0, defaultdict(int)
        for i in range(n):
            c = defaultdict(int)
            if nums[i] == k:
                co += 1
            c[nums[i]] = 1

            for val, freq in p.items():
                val = val & nums[i]
                if val == k:
                    co += freq
                c[val] += freq

            p = c

        return co
