#include <unordered_map>
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int  n = nums.size();
        long long ans = 0;
        unordered_map<long long,long long> pr;
        for(int  i=0;i<n;i++){
            unordered_map<long long,long long> cr;
            if(nums[i]==k) ++ans;
            cr[nums[i]]=1;
            for(auto& [key,val]:pr){
                long long nar = key&nums[i];
                if(nar == k) ans+=val;
                    cr[nar]+=val;
            }
            pr=cr;
        }
        return ans;


    }
};
