class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> prev;

        for (int i = 0; i <n;i++) {
            unordered_map<long long, long long> curr;
            if (nums[i] == k) {
                ++ans;
            }
            curr[nums[i]] = 1;
            for (auto& j : prev) {
                long long newval = j.first & nums[i];
                if (newval == k) {
                    ans += j.second;
                }
                curr[newval] += j.second;
            }

            prev = curr;
        }

        return ans;   
    }
};
