class Solution {
  public long countSubarrays(int[] nums, int k) {
    long cnt = 0;
    int n = nums.length;
    HashMap<Integer, Integer> hm = new HashMap<Integer, Integer>();

    for (int i = 0; i < n; i++) {
      HashMap<Integer, Integer> curr = new HashMap<Integer, Integer>();
      if (nums[i] == k)
        cnt++;
      curr.put(nums[i], curr.getOrDefault(nums[i], 0) + 1);
      for (int key : hm.keySet()) {
        int freq = hm.get(key);
        int temp = key & nums[i];
        if (temp == k)
          cnt += freq;

        curr.put(temp, curr.getOrDefault(temp, 0) + freq);
      }

      hm = curr;
    }

    return cnt;
  }
}
