#define ll long long
class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<ll,ll> st;
        ll ans=0;
        for(auto i:nums){
            unordered_map<ll,ll> tst;

            for(auto j:st){

                ll a=j.first;

                tst[(a&i)]+=st[a];

            }
            tst[i]++;
            for(auto j:tst)
            {    ll t=j.first;
                if(t==k)
                    ans+=tst[t];
            }

            st=tst;


        }

        return ans;


    }
};
