class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans=0,n=a.size();
        vector<vector<int>> v(n,vector<int> (32,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<32;j++){
                if(a[i]&(1ll<<j)){
                    v[i][j]=1;
                    if(i>0) v[i][j]+=v[i-1][j];
                }
            }
            int mn=i+1,mx=0;
            for(int j=0;j<32;j++){
                if(k&(1ll<<j)) mn=min(mn,v[i][j]);
                else mx=max(mx,v[i][j]);
            }
            // cout<<mx<<" "<<mn<<endl;
            if((mn-mx)>0) ans+=1ll*(mn-mx);
        }

        return ans;
    }
};
