class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {
         int size = nums.size();
         long long total = 0;
        unordered_map<long long, long long> mp;
        for (int i = 0; i < size; i++) {
            unordered_map<long long, long long> currmp; 
            if (nums[i] == k) {
                total++;
            }
            currmp[nums[i]] = 1;
            for (auto& [value, frequency] : mp) {
                long long ans = value & nums[i];
                if (ans == k) {
                    total += frequency;
                }
                currmp[ans] += frequency;
            }

            mp = currmp;
        }
        return total;

    }
};
