#include <vector>
#include <unordered_map>
#define ll long long
using namespace std;
class Solution {
public:
    long long countSubarrays(std::vector<int>& nums, int k) {
        unordered_map<int, int> count;
        ll ans = 0;
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            int num = nums[i];
            std::unordered_map<int, int> temp_count;

            if (num == k) {
                ++ans;
            }

            for (const auto& [key, value] : count) {
                int new_key = key & num;
                if (new_key >= k) {
                    temp_count[new_key] += value;
                    if (new_key == k) {
                        ans += value;
                    }
                }
            }

            temp_count[num]++;
            count = move(temp_count);
        }

        return ans;
    }
};
