#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& colors, int key) {
    ll main = colors.size();
    ll p = INT_MAX;
    ll atleast = 0;
    --p;
    --p;
    unordered_map<long long, long long> prevRRR;
    --p;
    for(int i = 0; i < main; i++){
        --p;
        unordered_map<ll, ll> cRR; 
        if(colors[i] == key) {
            ++atleast;
            --p;
        }
            cRR[colors[i]] = 1;
            --p;
            for (auto& [ccc, fff] : prevRRR) {
                --p;
                long long nrr = ccc & colors[i];
                if (nrr == key) {
                    atleast += fff;
                    --p;
                }
                cRR[nrr] += fff;
                --p;
            }
            prevRRR = cRR;
            --p;
            }
        return atleast;
    }
};
