class Solution {

    ll f(auto &arr,int k){
        int n=arr.size();

        ll ans=0;
        int i=0,j=0;
        // unordered_map<int,int>one,zero;
        vector<int>one(31,0),zero(31,0);

        int And=~0;

        while(j<n){

            for(int bit=0;bit<=30;bit++){
                if(arr[j]&(1<<bit)){
                    one[bit]++;
                }
                else{
                    zero[bit]++;
                }
            }

            And=And&arr[j];

            while(i<=j && And<k){

                for(int bit=0;bit<=30;bit++){
                    if(arr[i]&(1<<bit)){
                        one[bit]--;

                    }
                    else{
                        zero[bit]--;

                        if(zero[bit]==0){
                            And=And|(1<<bit);
                        }
                    }
                }
                i++;
            }
            // cout<<And<<endl;
            if(i<=j){
                ans+=j-i+1;
            }

            j++;
        }
        return ans;
    }
public:
    long long countSubarrays(vector<int>& arr, int k) {

        return f(arr,k)-f(arr,k+1);
    }
};
