class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        total = 0
        aa = defaultdict(int)

        for i in range(n):
            km = defaultdict(int)

            if nums[i] == k:
                total += 1

            km[nums[i]] = 1

            for v, f in aa.items():
                hb = v & nums[i]

                if hb == k:
                    total += f

                km[hb] += f

            aa = km

        return total
