class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n = nums.size();

        long long ans = 0;

        unordered_map<long long, long long> pR;

        for (int i = 0; i < n; i++) {

            unordered_map<long long, long long> cR; 

            if (nums[i] == k) {
                ans++;
            }

            cR[nums[i]] = 1;

            for (auto& [value, frequency] : pR) 
            {
                long long newRes = value & nums[i];
                if (newRes == k) {
                    ans += frequency;
                }
                cR[newRes] += frequency;
            }

            pR = cR;
        }
        return ans;
    }
};
