class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long int cnt = 0;
        int n = nums.size();
        unordered_map<int, int>freq1;

        for (int i = 0; i < n; i++) {
            unordered_map<int, int> freq2;
            if (nums[i] == k) {
                cnt++;
            }
            freq2[nums[i]]++;
            for (auto & entry : freq1) {
                int newAnd = entry.first & nums[i];
                if (newAnd == k) {
                    cnt += (long long) entry.second;
                }
                freq2[newAnd] += entry.second;
            }

            freq1 = freq2;
        }

        return cnt;
    }
};
