class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        prefix_count = [defaultdict(int) for _ in range(len(nums))]
        prefix_count[0][nums[0]] = 1
        result = 0

        for i in range(1, len(nums)):
            current_num = nums[i]
            prefix_count[i][current_num] += 1

            for key, count in prefix_count[i - 1].items():
                new_and = key & current_num
                prefix_count[i][new_and] += count

        for i in range(len(nums)):
            for key, count in prefix_count[i].items():
                if key == k:
                    result += count

        return result
