class Solution {
public:

    long long int helper(vector<int>&nums , int start , int end , int k) {
        if (start>end) {
            return 0;
        }
        long long int curr=0;
        long long int prev=0;
        long long int prev2=0;
        for (int i=start;i<=end;i++) {
            if (nums[i]==k) {
                curr+=(end-i+1);
                continue;
            }
            if (i>start && nums[i]==nums[i-1]) {
                curr+=(prev2);
                continue;
            }
            int andy=nums[i];
            bool anss=false;
            for (int j=i;j<=end;j++) {
                andy&=nums[j];
                if (andy==k) {
                    curr+=(end-j+1);
                    anss=true;
                    prev=j;
                    prev2=end-j+1;
                    break;
                }
            }
            if (anss==false) {
                prev2=0;
            }
        }
        return curr;
    }
    long long countSubarrays(vector<int>& nums, int k) {

        long long int ans=0;
        int prev=0;

        for (int i=0;i<nums.size();i++) {
            bool l=true;
            for (int j=0;j<32;j++) {
                int n=k>>j;
                n&=1;
                int m=nums[i]>>j;
                m&=1;
                if (n==1 && m==0) {
                    ans+=helper(nums,prev,i-1,k);
                    prev=i+1;
                }
            }
        }
        ans+=helper(nums,prev,nums.size()-1,k);
        return ans;

    }
};
