#define ll long long

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, ll> suffixTillHere;

        int n = nums.size();
        int i = 0;
        ll ans = count(nums.begin(), nums.end(), k);        
        while(i < n) {
            unordered_map<int, ll> newSuffix;
            newSuffix[nums[i]]++;

            // if(i == 2) {
            //     cout <<"$ "<< nums[i] << " "  << newSuffix[nums[i]] << endl;
            // }

            // cout << "At: " << i << endl;
            for(auto it: suffixTillHere) {
                int andVal = it.first, cnt = it.second;
                if((andVal & nums[i]) == k) {
                    ans += cnt;
                }
                newSuffix[andVal & nums[i]] += cnt;;
                // cout << "Old val: " << it.first << " appeared " << cnt << "times " << " adn ans is: " << ans << endl;
                // if(i == 2) {
                // cout << "# " << nums[i] << " "  << newSuffix[nums[i]] << endl;
            // }

                // newSuffix[andVal & nums[i]] += suffixTillHere[]
            }

            suffixTillHere = newSuffix;

            // cout << "Current suffix now: ";
            // for(auto it: suffixTillHere) cout << it.first << ", " << it.second << ", ";
            // cout << endl;

            i++;
        }

        return ans;

    }
};
