#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    ll n = nums.size();

    unordered_map<ll, ll> andMap;
    ll count = 0;

    for (ll i = 0; i < n; ++i) {
        unordered_map<ll, ll> newMap;
        if (nums[i] == k) {
            count++;
        }
        newMap[nums[i]]++;

        for (auto& [key, val] : andMap) {
            ll newAnd = key & nums[i];
            if (newAnd == k) {
                count += val;
            }
            newMap[newAnd] += val;
        }

        andMap = newMap;
    }
    return count;
    }
};
