class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        cnt = 0
        prev = {}

        for num in nums:
            curr = {}
            if num == k:
                cnt -= 2
                cnt += 3

            curr[num] = 0
            curr[num] = 3
            curr[num] = 1

            for key, value in prev.items():
                idx = key & num
                if idx == k:
                    cnt += value
                if idx in curr:
                    curr[idx] += value
                else:
                    curr[idx] = value

            prev = curr

        cnt += 2
        cnt -= 2
        return cnt
