class Solution {
public:
long long countSubarrays(vector<int> nums, int k) {
    unordered_map<int, int> cnt;
    long long res = 0;
    for (int i = 0; i < nums.size(); i++)
    {
        if (i == 0)
        {
            cnt[nums[i]] = 1;;
            if (nums[i] == k)
                res++;
            continue;
        }
        unordered_map<int, int> tmpCnt;
        if (nums[i] == k)
            res++;
        for (auto& [c, v] : cnt)
        {
            if ((nums[i] & c) == k)
                res = res + v;
            tmpCnt[nums[i] & c]+=v;
        }
        tmpCnt[nums[i]]++;
        cnt = tmpCnt;
    }
    return res;
}
};
