class Solution {
    typedef long long ll;
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll  cnt=0;
        int n=nums.size();
        unordered_map<ll,ll>mp;
        for(int i=0;i<n;++i)
        {
            unordered_map<ll,ll>mp1;
            if(nums[i]==k)
            {
                cnt++;
            }
                mp1[nums[i]]=1;
            for (const auto& x : mp) {
                int a=x.first;
                int b=x.second;
                int j=a & nums[i];
                if (j==k) {
                    cnt+=b;
                }
                if (mp1.find(j)!=mp1.end()) {
                    mp1[j]+=b;
                } else {
                    mp1[j]=b;
                }
            }
            mp =mp1;
        }
        return cnt;
    }
};
