#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans=0;
        ll n=nums.size();
        map<ll,ll> f;
        for(int i=0;i<n;i++)
        {
            if(nums[i]==k) ++ans;
            map<ll,ll> ref;
            ++ref[nums[i]];
            // for(auto [x,y]:f) cout<<x<<" "<<y<<endl;
            // cout<<endl;
            // for(auto [x,y]:ref) cout<<x<<" "<<y<<endl;
            // cout<<endl;
            for(auto [x,y]:f)
            {
                if((x&nums[i])==k) ans+=y;
                ref[(x&nums[i])]+=y;
            }
            f=ref;
        }
        return ans;
    }
};
