class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> m;
        long long ans=0;
        for(int i=0;i<nums.size();++i){
            int x = nums[i];
            unordered_map<int, int> new_m;
            if(x == k){
                ans++;
            }
            new_m[x]++;
            for(auto &[s, v]:m){
                if((s & x)==k){
                    ans+=v;
                }
                new_m[s & x] += v;
            }
            swap(m, new_m);
        }
        return ans;

    }
};
