class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<long long, long long> mp;
        for(auto i: nums){
            unordered_map<long long, long long> temp;
            if(i==k) ans++;
            temp[i]=1;
            for(auto it: mp){
                long long curr = i&it.first;
                if(curr==k) ans+=it.second;
                temp[curr]+=it.second;
            }
            mp = temp;
        }
        return ans;
    }
};
