class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ans++;
            }
            curr[nums[i]] = 1;
            for (auto& [v, freq] : prev) {
                long long andval = v & nums[i];
                if (andval == k) {
                    ans = ans + freq;
                }
                curr[andval] = curr[andval] + freq;
            }

            prev = curr;
        }
        return ans;
    }
};
