class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> pmp;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cmp; 
            if (nums[i] == k)   ans++;

            cmp[nums[i]] = 1;
            for (auto& [v, f] : pmp) {
                long long newand = v & nums[i];
                if (newand == k) {
                    ans += f;
                }
                cmp[newand] += f;
            }
            pmp = cmp;
        }
        return ans;
    }
};
