class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
          int sZZe = nums.size();
        long long TtL = 0;
        unordered_map<long long, long long> PrS;
        for (int index = 0; index < sZZe; ++index) {
            unordered_map<long long, long long> CrS; 
            if (nums[index] == k) {
                ++TtL;
            }
            CrS[nums[index]] = 1;
            for (auto& [value, frequency] : PrS) {
                long long newAndResult = value & nums[index];
                if (newAndResult == k) {
                    TtL += frequency;
                }
                CrS[newAndResult] += frequency;
            }

            PrS = CrS;
        }
        return TtL;


    }
};
