class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, long long> dp;
        long long c = 0;
        for (int i = 0; i < nums.size(); i++) 
        {
            unordered_map<int, long long> ndp;
            for (auto ele : dp)
            {
                int nand = ele.first & nums[i];
                ndp[nand] += ele.second;
            }
            ndp[nums[i]] += 1;
            if (ndp.find(k) != ndp.end()) 
            {
                c += ndp[k];
            }
            dp = ndp;
        }

    return c;
    }
};
