class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
          int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> previous;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> current; 
            if (array[index] == target) {
                ++total;
            }
            current[array[index]] = 1;
            for (auto& [value, frequency] : previous) {
                long long new1 = value & array[index];
                if (new1 == target) {
                    total += frequency;
                }
                current[new1] += frequency;
            }

            previous = current;
        }
        return total;
    }
};
