class Solution { 
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size(); 
        long long ans = 0;

        unordered_map<long long int, long long> pr; 
          unordered_map<long long int , long long> cr;
         int i=0;
         while(i<n){

         if(k==nums[i]){
                ans++;

            }
             cr[nums[i]]=1;
            for(auto it:pr){
                int val=(it.first&nums[i]);
                if(k==val){
                    ans+=it.second;
                }
                cr[val]+=it.second;
            }
             pr=cr;
             cr.clear();
             i++;
         }



        return ans;
    }



};
