class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> count;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> new_count;

            for (auto& [key, value] : count) {
                int new_and = key & nums[i];
                if (new_and == k) {
                    ans += value;
                }
                if (new_and >= k) {
                    new_count[new_and] += value;
                }
            }

            if (nums[i] == k) {
                ans++;
            }
            if (nums[i] >= k) {
                new_count[nums[i]]++;
            }

            count = new_count;
        }

        return ans;

    }
};
