class Solution {
public:
    long long countSubarrays(vector<int>& a, int b) {
        int c = a.size();
        long long d = 0;
        map<int, int> e;

        for (int f = 0; f < c; ++f) {
            map<int, int> g;
            if (a[f] == b) {
                ++d;
            }
            g[a[f]] = 1;

            for (auto h : e) {
                int i = h.first & a[f];
                int j = h.second;
                if (i == b) {
                    d += j;
                }
                g[i] += j;
            }
            e = g;
        }

        return d;
    }
};
