class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long total = 0;
        unordered_map<long long, long long> pre;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                ++total;
            }

            curr[nums[i]] = 1;

            for (auto [v, f] : pre) {
                long long x = v & nums[i];
                if (x == k) {
                    total += f;
                }
                curr[x] += f;
            }

            pre = curr;
        }

        return total;
    }
};
